import React from 'react';
import ReactDOM from 'react-dom';

const Modal = props => {
    return ReactDOM.createPortal(
        <div style={{
            backgroundColor: '#f4f4f4',
            zIndex: 10,
            height: '100vh',
            width: '100vw',
            top: '0',
            left: '0',
            position: 'fixed',
            textAlign: 'center',
        }}>
            <div style={{
                height: '100vh',
                
            }}>
                Modal
            </div>
        </div>,
        document.getElementById('modal')
    );
}

export default Modal;
