import React from 'react';
import ReactDOM from 'react-dom';

const Modal = props => {
    return ReactDOM.createPortal(
        <div style={{
            backgroundColor: '#444',
            zIndex: 10,
            height: '100vh',
            width: '100vw',
            top: '0',
            left: '0',
            position: 'fixed',
            textAlign: 'center',
            opacity: 0.9
        }}>
            <div style={{
                opacity: 1,
                border: '3px solid black',
                margin: '20vh auto',
                width: '30vw',
                lineHeight: '40vh',
                backgroundColor: 'white',
                color: 'black'
            }}>
                Modal
            </div>
        </div>,
        document.getElementById('modal')
    );
}

export default Modal;
